Number.prototype.formatBytes=function(){let t=["B","KB","MB","GB","TB"],r=this,e;for(e=0;r>=1024&&e<4;e++)r/=1024;return r.toFixed(2)+t[e]};String.prototype.cleanUrl=function(){return this.replace(/\?.+/,"")};String.prototype.unSlug=function(){return this.replace(/[_-]/g," ")};String.prototype.capitalize=function(){return this.toLowerCase().replace(/\w/,t=>t.toUpperCase())};Array.prototype.shuffle=function(){const t=[...this];for(let r=t.length-1;r>0;r--){const e=Math.floor(Math.random()*(r+1));[t[r],t[e]]=[t[e],t[r]]}return t};
